package nuestroPrimerPrograma;

public class PracticaVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hola mundo");
		
		
		int edad = 35;
		System.out.println(edad);
		
		edad = 58;
		System.out.println("La variable edad ha cambiado, ahora su valor es " + edad);
		
		edad = 92;
		System.out.println("La edad ha vuelto a cambiar durante la ejecución del programa "+ edad);
		
	}

}
